import React, {Component} from 'react';
import axios from 'axios';

import './CurrentIssue.css'

const Task = props => (
  <div className='indv-issue'>
            <p className='close'> Close Issue</p>
            <p>Assigned <span>{props.task.user}</span></p>
            <p>Priority <span>{props.task.priority}</span></p>
            <p>Description</p>
            <p className='description'>{props.task.description}</p>
            <hr />
          </div>
    )

class CurrentIssue extends Component {

  constructor(props) {
    super(props);

    // this.deleteExercise = this.deleteExercise.bind(this)

    this.state = {task: [{
      "user":"alex",
      "priority":"high",
      "description":"Test"
    },{
      "user":"alex",
      "priority":"high",
      "description":"Test"
    }]};
  }

  componentDidMount() {
    axios.get('http://localhost:5000/tasks/')
      .then(response => {
        this.setState({ tasks: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  // deleteExercise(id) {
  //   axios.delete('http://localhost:5000/exercises/'+id)
  //     .then(response => { console.log(response.data)});

  //   this.setState({
  //     exercises: this.state.exercises.filter(el => el._id !== id)
  //   })
  // }

  taskList() {
    return this.state.task.map(currenttask => {
      return <Task task={currenttask} key={currenttask._id}/>;
    })
  }

  render(){
    return (
      <div className="current-issues">
         { this.taskList() }
      </div>
    );
  }
}

export default CurrentIssue;
