import React from 'react';
import axios from 'axios';
import './AddIssue.css'

function AddIssue(props) {

constructor(props) 
{
    this.onChangeUser = this.onChangeUser.bind(this);
    this.onChangePriority = this.onChangePriority.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);

    this.state = {
      user: '',
      priority: '',
      description: '',
      tasks:[]
    }
  }

  componentDidMount() 
  {
    axios.get('http://localhost:5000/users/')
      .then(response => {
        if (response.data.length > 0) {
          this.setState({
            tasks: response.data.map(user => user.user),
            user: response.data[0].user
          })
        }
      })
      .catch((error) => {
        console.log(error);
      })

  }

  onChangeUser(e) 
  {
    this.setState({
      user: e.target.value
    })
  }

  onChangePriority(e) 
  {
    this.setState({
      priority: e.target.value
    })
  }

  onChangeDescription(e) 
  {
    this.setState({
      description: e.target.value
    })
  }


  onSubmit(e)
  {
    e.preventDefault();
    const task = {
      user: this.state.user,
      priority: this.state.priority,
      description: this.state.description,
    }

    console.log(task);

    axios.post('http://localhost:5000/tasks/add', task)
      .then(res => console.log(res.data));

    window.location = '/';

}

  return (
    <div className="add-issue">
        <form onSubmit={this.onSubmit}>
            <label>Description<input type="text" placeholder='Description of Bug...'/></label>
            <label> Assign To
                <select name="devSelect" id="devSelect" placeholder='Please Assign Bug to Be Fixed By Developer'>
                    <option value="Alex">Alex</option>
                    <option value="Daniel">Daniel</option>
                    <option value="John">John</option>
                </select>
            </label>
            <label> Priority Level
                <select name="levSelect" id="levSelect" placeholder='Please Select Level of Urgency to Fix Bug'>
                    <option value="Low">Low</option>
                    <option value="Medium">Medium</option>
                    <option value="High">High</option>
                    <option value="Extreme">Extreme</option>
                </select>
            </label>
            <button type="submit">Add</button>
        </form>
    </div>
  );
}

export default AddIssue;
